  private trace(event: string, data?: Record<string, unknown>): void {
    if (this.tracingEnabled) {
      const timestamp = new Date().toISOString();
      console.log(`[ABACO_TRACE] ${timestamp} - ${this.operationId} - ${event}`, data || {});
